import re
class Solution:
    def countBinarySubstrings(self, s: str) -> int:
        s = s.replace("01", "0 1")
        s = s.replace("10", "1 0")
        s = s.split(" ")
        count = 0
        for i in range(len(s) - 1):
            count += min(len(s[i]), len(s[i + 1]))
        return count








s =  "00110011"
s = "10101"
s = ""
